//使用require载入http模块，将实例化后的HTTP赋值给http变量
var http = require('http');
//利用createServer()方法创建服务器，并绑定8083端口
//接收参数request接收请求，response响应请求
http.createServer(function(request,response){
	//发送HTTP头部
	//状态200
	//内容类型text/plain
	response.writeHead(200,{'Content-Type':'text/plain'});
	response.end('Hello node.js');
}).listen(8083);
console.log('server running at 192.168.1.14:8083');

